import React from "react";
import { useState, useEffect } from "react";
import { Navigate, useNavigate } from "react-router-dom";
import axios from "axios";

function Cart() {
  const navigate = useNavigate();
  const [cartitems, setcartitems] = useState([]);
  const [total,settotal]=useState([])



  const userid = localStorage.getItem("userid");
  const carttotal=0





  const getitem = () => {
    const name = { username: userid };
    axios
      .post("http://127.0.0.1:8000/cart/usercart/", name)
      .then((Response) => {
        console.log(Response.data);
        setcartitems(Response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    const name = { username: userid };
    axios
      .post("http://127.0.0.1:8000/cart/usercart/", name)
      .then((Response) => {
        console.log(Response.data);
        setcartitems(Response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);



  useEffect(() => {
    let grandtotal1=0
      cartitems.forEach(element => {
        grandtotal1=grandtotal1+element.sub_total
    });
    settotal(grandtotal1)



  }, [cartitems])



  const removeitem = (id) => {
    console.log(id);
    console.log("this is  remove the item");
    // const url = "http://127.0.0.1:8000/cart/"+id;
    axios
      .delete(`http://127.0.0.1:8000/cart/removeitem/${id}`)
      .then((Response) => {
        console.log(Response.data);
        getitem();
        console.log("this is then");
      })
      .catch((error) => {
        console.log("this is error");
      });
  };

  const countincrease = (stock, product_id) => {


    console.log("this is product add button");
    const product_stock = stock;
    const prod_id = product_id;
    console.log(prod_id);
    const name = userid;
    const action = "add";
    const data = {
      product_id: prod_id,
      user_id: name,
      product_stock: product_stock,
      action: "add",
    };

    console.log("this is increase");
    axios.patch("http://127.0.0.1:8000/cart/additem/",data).then((Response)=>{
      console.log("this is then")
      console.log(Response.data)
      getitem()
    }).catch((error)=>{
      console.log("this is error")
    })
  };

  const countdecrease = (stock, product_id) => {



    console.log(stock, "this is stock");

    console.log("this is product add button");
    const product_stock = stock;
    const prod_id = product_id;
    console.log(prod_id);
    const name = userid;
    const action = "not add";
    const data = {
      product_id: prod_id,
      user_id: name,
      product_stock: product_stock,
      action: "not added",
    };

    console.log("this is decrease");
    axios.patch("http://127.0.0.1:8000/cart/additem/",data).then((Response)=>{
      console.log("this is then")
      console.log(Response.data)
      getitem()
    }).catch((error)=>{
      console.log("this is error")
    })
  };

  return (
    <div>
      <div>
        <div
          className="w-full h-full bg-white  top-0 overflow-y-auto overflow-x-hidden fixed sticky-0"
          id="chec-div"
        >
          <div
            className="w-full absolute z-10 right-0 h-full overflow-x-hidden transform translate-x-0 transition ease-in-out duration-700"
            id="checkout"
          >
            <div className="flex md:flex-row flex-col justify-end" id="cart">
              <div
                className="lg:w-1/2 w-full md:pl-10 pl-4 pr-10 md:pr-4 md:py-12 py-8 bg-white overflow-y-auto overflow-x-hidden h-screen"
                id="scroll"
              >
                <div className="flex items-center text-gray-500 hover:text-gray-600 cursor-pointer">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    className="icon icon-tabler icon-tabler-chevron-left"
                    width={16}
                    height={16}
                    viewBox="0 0 24 24"
                    strokeWidth="1.5"
                    stroke="currentColor"
                    fill="none"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                  >
                    <path stroke="none" d="M0 0h24v24H0z" fill="none" />
                    <polyline points="15 6 9 12 15 18" />
                  </svg>
                  <p
                    className="text-sm pl-2 leading-none"
                    onClick={() => {
                      navigate("/");
                    }}
                  >
                    Back
                  </p>
                </div>
                <p className="text-5xl font-black leading-10 text-gray-800 pt-3">
                  Bag
                </p>
                {cartitems &&
                  cartitems.map((obj, id) => {
                    return (
                      <div className="md:flex items-center mt-14 py-8 border-t border-gray-200">
                        <div className="w-1/4">
                          <img
                            src={obj.image_url}
                            alt
                            className="w-full h-full object-center object-cover"
                          />
                        </div>
                        <div className="md:pl-3 md:w-3/4">
                          <p className="text-xs leading-3 text-gray-800 md:pt-0 pt-4">
                            RF293
                          </p>
                          <div className="flex items-center justify-between w-full pt-1">
                            <p className="text-base font-black leading-none text-gray-800">
                              {obj.products}
                            </p>
                            <button className="py-2 px-1 border border-gray-200 mr-6 focus:outline-none">
                              {/* <option>03</option> */}
                              {obj.product_stock}
                            </button>
                          </div>

                          <p className="w-96 text-xs leading-3 text-gray-600">
                            Composition: 100% calf leather
                          </p>
                          <div className="flex items-center justify-between pt-5 pr-6">
                            <div className="flex itemms-center">
                              <p className="text-xs leading-3 underline text-gray-800 cursor-pointer">
                                Add to favorites
                              </p>
                              <p
                                className="text-xs leading-3 underline text-red-500 pl-5 cursor-pointer"
                                onClick={() => {
                                  removeitem(obj.id);
                                }}
                              >
                                Remove
                              </p>
                              <p className="text-xs leading-3 underline text-red-500 pl-5 cursor-pointer">
                                cart-quantity{obj.product_stock}
                              </p>
                            </div>
                            <p className="text-base font-black leading-none text-gray-800">
                              ${obj.sub_total}
                            </p>
                          </div>


                          <button
                            data-action="decrement"
                            className=" bg-gray-300 text-gray-600 hover:text-gray-700 hover:bg-gray-400 h-full w-20 rounded-l cursor-pointer outline-none"
                            onClick={() => {
                              countdecrease(obj.product_stock, obj.product_id);
